SELECT id,csr,status
FROM regapp
WHERE user = ${axway.param.username} 
and cod_verification= ${axway.param.code} 
and id_device= ${axway.param.deviceID}
and TIME_TO_SEC(TIMEDIFF(sysdate(), date_create))<=${axway.expiry.activation.code.seconds}

function invoke(msg)         {          

    var activation_code = "";
    var possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";  
  
    for( var i=0; i < 10; i++ )  {
       activation_code += possible.charAt(Math.floor(Math.random() * possible.length));  
    }

 	
	msg.put("axway.activation.code",activation_code);

  return true;         
}

